import dayjs from "dayjs";

import { ConsumeItem } from "../atoms";
import { getRatingColors } from "../common/ShowRating";
import { getDescriptiveQuantity } from "./index";

const cssStyleSheet = `
      <style type="text/css">
      @font-face {
        font-family: 'Metropolis';
        src: url(data:font/truetype;charset=utf-8;base64,) format('truetype');
        font-weight: normal;
        font-style: normal;
    }

    table { 
        width: 750px; 
        border-collapse: collapse; 
        margin:50px auto;
        }
    
    /* Zebra striping */
    tr:nth-of-type(odd) { 
        background: #eee; 
        }
    
    th { 
        background: #3498db; 
        color: white; 
        font-weight: bold; 
        }
    
    td, th { 
        padding: 10px; 
        border: 1px solid #ccc; 
        text-align: left; 
        font-size: 18px;
        }
    
    /* 
    Max width before this PARTICULAR table gets nasty
    This query will take effect for any screen smaller than 760px
    and also iPads specifically.
    */
    @media 
    only screen and (max-width: 760px),
    (min-device-width: 768px) and (max-device-width: 1024px)  {
    
        table { 
              width: 100%; 
        }
    
        /* Force table to not be like tables anymore */
        table, thead, tbody, th, td, tr { 
            display: block; 
        }
        
        /* Hide table headers (but not display: none;, for accessibility) */
        thead tr { 
            position: absolute;
            top: -9999px;
            left: -9999px;
        }
        
        tr { border: 1px solid #ccc; }
        
        td { 
            /* Behave  like a "row" */
            border: none;
            border-bottom: 1px solid #eee; 
            position: relative;
            padding-left: 50%; 
        }
    
        td:before { 
            /* Now like a table header */
            position: absolute;
            /* Top/left values mimic padding */
            top: 6px;
            left: 6px;
            width: 45%; 
            padding-right: 10px; 
            white-space: nowrap;
            /* Label the data */
            content: attr(data-column);
    
            color: #000;
            font-weight: bold;
        }
    
    }
    
      </style>`;

export enum FilterNames {
  Today = "Today",
  All = "All",
  Custom = "Custom",
}

export const getHtmlReportString = (
  historyData: ConsumeItem[],
  selectedFilter: FilterNames.Today | FilterNames.All | FilterNames.Custom,
  message: string
) => {
  const html = `<!DOCTYPE html><head><title>Invoice</title>${cssStyleSheet}</head>`;

  const isToday = selectedFilter === FilterNames.Today;

  const transactionsInfo = historyData.map((each) => {
    return `<tr>
          <td>${
            isToday
              ? dayjs(each.date).format("hh:mm a")
              : dayjs(each.date).format("DD/MM/YYYY, hh:mm a")
          }</td>
          <td>${each.item.name}</td>
          <td>${getDescriptiveQuantity(each.item, each.quantity)}</td>
          <td style="color: ${getRatingColors(each.rating)}">
            ${each.rating.label}
          </td>
          </tr>`;
  });

  const resultString = `
    ${html}
<body> 
<h2 style="text-align: center;">Diet Chart</h2>
<h3 style="line-height: 1.6;">${selectedFilter}</h3>
<table>
<thead>
  <tr>
    <th>${isToday ? "Time" : "Date & Time"}</th>
    <th>Food Item Name</th>
    <th>Quantity</th>
    <th>Rating(Feeling level)</th>
  </tr>
</thead>
<tbody>
  ${transactionsInfo.join(" ")}
</tbody>
</table>
<footer>
${message ? `<p>Message for Nutritionist: \n${message}</p>` : ""}
</footer>
</body>
  `;

  return resultString;
};
